  <div class="alert alert-danger">
    <h4>Ooops, there was a problem</h4>
       <ul>
         @foreach($errors->all() as $message)
         <li>{{ $message }}</li>
          @endforeach
       </ul>
     </div>
@endif

$rules = array(
    'name'        => 'required',
);

$validator = Validator::make(Input::all(), $rules);

// Validation fails
if ($validator->fails())
{
    return Redirect::back()->withErrors($validator)->withInput();
}

else
{


}

@if (Session::has('flash_error'))
    <div class="alert alert-danger"><button type="button" class="close" data-dismiss="alert" aria-hidden="true">&times;</button>{{ Session::get('flash_error') }}</div>
@endif

@if (Session::has('flash_notice'))
    <div class="alert alert-info"><button type="button" class="close" data-dismiss="alert" aria-hidden="true">&times;</button>{{ Session::get('flash_notice') }}</div>
@endif

@if (Session::has('flash_success'))
    <div class="alert alert-success"><button type="button" class="close" data-dismiss="alert" aria-hidden="true">&times;</button>{{ Session::get('flash_success') }}</div>
@endif

$data = array(
           'fname' => $fname,
           'lname' => $lname,
           'email'     => $email,
           'username'  => $username
       );
       // Send the email
       Mail::send('emails/registration', $data, function( $message ) use ($data)
       {
